pragma solidity ^0.4.4;

import 'zeppelin-solidity/contracts/ownership/Ownable.sol';

contract QuestionFactory is Ownable {
  event NewQuestion(uint questionId, string _text);

  struct Question {
    string text;
  }

  Question[] public questions;

  mapping (uint => address) public questionToOwner;
  mapping (address => uint) ownerQuestionCount;


  modifier ownerOf(uint _questionId) {
    require(msg.sender == questionToOwner[_questionId]);
    _;
  }

  function createQuestion(string _text) public {
    uint id = questions.push(Question(_text)) -1;
    questionToOwner[id] = msg.sender;
    ownerQuestionCount[msg.sender]++;
    NewQuestion(id, _text);
  }

  function getQuestionsByOwner(address _owner) external view returns(uint[]) {
    uint[] memory result = new uint[](ownerQuestionCount[_owner]);
    uint counter = 0;

    for (uint i = 0; i < questions.length; i++) {
      if (questionToOwner[i] == _owner) {
        result[counter] = i;
        counter++;
      }
    }
    return result;
  }

  function getQuestion(uint _questionId) external view returns(string){
    return questions[_questionId].text;
  }

  function allQuestions() constant returns(Question[]) {
    return questions;
  }
}
